mapper.insert:
insert into favorite_canteen ( favorite_id, canteen_id
  )
values ( #{favoriteId,jdbcType=INTEGER}, #{canteenId.canteenId,jdbcType=INTEGER}
  )

mapper.update:
update favorite_canteen
set favorite_id = #{favoriteId,jdbcType=INTEGER},
  canteen_id = #{canteenId.canteenId,jdbcType=INTEGER}
where favorite_canteen_id = #{favoriteCanteenId,jdbcType=INTEGER}

mapper.select:
select favorite_canteen_id, favorite_id, canteen_id
from favorite_canteen
where favorite_canteen_id = #{favoriteCanteenId,jdbcType=INTEGER}

select fc.*,c.* 
from favorite_canteen fc left join canteen c on fc.canteen_id=c.canteen_id
  where fc.favorite_id = #{userId,jdbcType=INTEGER}
